package practice;

public class GreatestOrSmallest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		int b = 50;
		int c = 40;
		if (a > b && a > c) {
			System.out.println("a is greatest");
		} else if (b > a && b > c) {
			System.out.println("b is greatest");
		} else {
			System.out.println("c is greatest");
		}
	}

}
